    private void SisaBagiMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_SisaBagiMouseClicked
           angka1 = Double.parseDouble(txtangka1.getText());
        angka2 = Double.parseDouble(txtangka2.getText());
        
        hasil = angka1 % angka2;
        
        
        txthasil.setText(String.valueOf(hasil));
    }//GEN-LAST:event_SisaBagiMouseClicked

    private void PangkatMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_PangkatMouseClicked
           try {
        double base = Double.parseDouble(txtangka1.getText());
        double exponent = Double.parseDouble(txtangka2.getText());

        double result = Math.pow(base, exponent);
        
        txthasil.setText(String.valueOf(result));
    } catch (NumberFormatException e) {
        txthasil.setText("Error: Invalid input");
    }
    }//GEN-LAST:event_PangkatMouseClicked

    private void AkarKuadratMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_AkarKuadratMouseClicked
          try {
        double operand = Double.parseDouble(txtangka1.getText());

        if (operand >= 0) {
            double result = Math.sqrt(operand);
            txthasil.setText(String.valueOf(result));
        } else {
            txthasil.setText("Error: Cannot calculate square root of a negative number");
        }
    } catch (NumberFormatException e) {
        txthasil.setText("Error: Invalid input");
    }
    }//GEN-LAST:event_AkarKuadratMouseClicked

